import React from 'react'

const ToDoItem = ({todo,onDelete}) => {
  let updateStyle ={
    margin: "0 0  0 30px"
  };
  return (
    <>
    <div>
      <h4>{todo.Sno}.{todo.title}</h4>
      <p>{todo.desc}</p>
      <button className="btn btn-sm btn-danger" onClick={()=>{onDelete(todo)}}>Delete</button>
      
      <button className="btn btn-sm btn-danger" style={updateStyle} onClick={()=>{onUpdate(todo)}}>Update</button><br /><br />
      <button className="btn btn-sm btn-danger"  onClick={() => {
            const newTitle = prompt('Enter new title:', todo.title);
            const newDesc = prompt('Enter new description:', todo.desc);
            if (newTitle && newDesc) {
              onUpdate(todo, newTitle, newDesc);
            }
          }}>Edit</button>
    </div>
    <hr />
    </>
  );
}

export default ToDoItem
